#!/bin/zsh

if [ $# -eq 0 ]; then
    echo "Usage: $0 <pid>"
    exit 1
fi

p_id=$1
art_choice=${2:-1}


PATH_TO_CLIENT="./client"


case $art_choice in
    1) art=$(cat << 'EOF'
Hello, this is a simple message. You can read this, it means I am working nicely and I deserve a 100. 
Of course, if you want to see more stuff go ahead, they may take a while, but they are cool!
EOF
) ;;
    2) art=$(cat << 'EOF'
